import { Component, HostListener, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AdvConstants } from '@app/entity/advertiser/_constants/AdvConstants';
import { AdvertiserService } from '@app/entity/advertiser/_services/advertiser.service';
import { AppConstants } from '@app/shared/_constants/AppConstants';
import { EntitiesService } from '@app/shared/_services/entities.service';
import { AdvertiserSettings, AppSettingsControllerService } from '@revxui/api-client-ts';
import { Observable } from 'rxjs';
import { UPLOAD_CONST } from '@app/entity/advertiser/_services/advertiser.service';

@Component({
  selector: 'app-advertiser-settings',
  templateUrl: './advertiser-settings.component.html',
  styleUrls: ['./advertiser-settings.component.scss']
})
export class AdvertiserSettingsComponent implements OnInit {

  images: string[] = [
    'https://www.lifewire.com/thmb/h63Knl7K7_bHLoxa-HBWPIAxKTY=/2576x2576/smart/filters:no_upscale()/random-numbers-over-blackboard-166043947-57bb63065f9b58cdfd31d1fe.jpg',
    'https://i.pinimg.com/474x/4c/d1/46/4cd14607a40c32a55f190eae25599578.jpg',
    'https://centralca.cdn-anvilcms.net/media/images/2019/08/08/images/random.max-752x423.png',
    'https://thumbs.dreamstime.com/z/wallpaper-geometric-elements-design-colors-random-layout-background-colorful-vector-illustration-121195762.jpg',
    'data:image/jpeg;base64,',
    'https://png.pngtree.com/thumb_back/fw800/background/20190813/pngtree-motive-design-of-random-pattern-color-combination-image_289703.jpg',
    'data:image/jpeg;base64,',
    'https://n3.sdlcdn.com/imgs/h/5/0/Capture-1e15a.JPG',
    'data:image/jpeg;base64,',
  ];

  advConst = AdvConstants;
  uploadConst = UPLOAD_CONST;
  formValidated = true;
  advId; advSettings;
  breadcrumbs: string;
  saveClicked: boolean; // to disable propmt on saving advertiser
  appConst = AppConstants;

  @HostListener('window:beforeunload', [])
  canDeactivate(): Observable<boolean> | boolean {
    if (this.saveClicked) {
      return true;
    }
    return false;
  }

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private advService: AdvertiserService,
    private entitiesService: EntitiesService
  ) { }

  ngOnInit() {
    this.saveClicked = false;
    this.advSettings = {} as AdvertiserSettings;

    this.route.parent.paramMap.subscribe(params => {
      // console.log(params.get('id'))
      const id = params.get('id');
      if (id && !isNaN(Number(id))) {
        this.advId = id;
        // this.getDetailsById(Number(id));
        this.getAdvSettings(Number(id));
      }

    });
  }

  getAdvSettings(id: number) {
    this.advService.getAdvSettings(id).subscribe(response => {
      // console.log('response ,', response);

      // REVX-450: AdvertiserSettings should have 2 additional parameter “logoLinkList” and “fallbackLinkList”
      // which holds the list of advertiser logo uploaded and list of fallback images uploaded

      this.advSettings = response.respObject;
    }, (error: any) => {
    });
  }

  save() {
    if (this.advId) {
      if (!this.advSettings.liftTestActive) {
        this.advSettings.liftTestActive = false;
      }
      if (!this.advSettings.eventFilterAllowed) {
        this.advSettings.eventFilterAllowed = false;
      }

      // REVX-450: Set “logoLinkList” and “fallbackLinkList” parameters in AdvertiserSettings as per the actions taken in the UI

      this.saveClicked = true;
      this.advService.updateAdvSettings(this.advId, this.advSettings).subscribe(resp => {
        if (resp && resp.respObject) {
          // console.log('resp ', resp);

          this.router.navigate(['/advertiser/details/' + resp.respObject.advertiserId]);
        }
      }, (error: any) => {
        this.saveClicked = false;
      });
    }
  }

  getDetailsById(id: number) {
    this.entitiesService.getDetailsById(id, AppConstants.ENTITY.ADVERTISER).subscribe(response => {
      const breadcrumbsObj = this.entitiesService.createBCObject(response.respObject);
      this.breadcrumbs = JSON.stringify(breadcrumbsObj);
    });
  }




}
